<?php/** Copyright (c) 2013 www.magebuzz.com */class Magebuzz_Affiliate_Block_Adminhtml_Request_ResponseFormContainer extends Mage_Adminhtml_Block_Widget_Form_Container {	public function __construct() {		parent::__construct();		$this->_headerText = Mage::helper('affiliate')->__('Response Form');		$this->removeButton('add');		$this->removeButton('delete', 'label', 'Reject');		$this->removeButton('reset');		$this->removeButton('save');		$this->addButton(			'reject', array(				'label'	=> 'Reject',				'class'	=> 'delete',				'onclick' => '$(editForm.formId).action = \''.$this->getUrl('affiliate/adminhtml_affiliate/reject').'\'; editForm.submit()'		));		$this->addButton('complete', array(			'label' => 'Accept',			'class'	=> 'save',			'onclick' => '$(editForm.formId).action = \''.$this->getUrl('affiliate/adminhtml_affiliate/complete').'\'; editForm.submit()'		));	}		protected function _prepareLayout() {		$this->setChild('form', $this->getLayout()->createBlock('affiliate/adminhtml_request_responseForm'));//->setSaveParametersInSession(true));	}}